import React, { form } from 'react';


export default class Default extends React.Component {
    constructor(props) {
    super(props)

    this.state = {
       name: '',
       age: '',
       salary: '',
       hobby: ''
    }
  }

  changeHandler = (e) => {
    this.setState({[e.target.name] : e.target.value})
  }

  submitHandler = e => {
    e.preventDefault();
    console.log(this.state);
  }

    render() {
        return (
            <div>
                <a href='/'>回到home</a>
                <form classname="form" onsubmit="{this.submitHandler}">
                    <form.field>
                      <label>Name</label>
                      <input placeholder="Enter your name" type="text" name="name" defaultValue="{name}" onchange="{this.changeHandler}/" />
                    </form.field>
                    <form.field>
                      <label>Age</label>
                      <input placeholder="Enter your age" type="number" name="age" defaultValue="{age}" onchange="{this.changeHandler}/" />
                    </form.field>
                    <form.field>
                      <label>Salary</label>
                      <input placeholder="Enter your salary" type="number" name="salary" defaultValue="{salary}" onchange="{this.changeHandler}/" />
                    </form.field>
                    <form.field>
                      <label>Hobby</label>
                      <input placeholder="Enter your hobby" type="text" name="hobby" defaultValue="{hobby}" onchange="{this.changeHandler}/" />
                    </form.field>
                    <button color="blue" type="submit">Submit</button>
                  </form>
            </div>
        )
    }
}
